mapper.select:
SELECT id, title, start_date AS startDate, end_date AS endDate, category, user_email
FROM calendar_events
WHERE user_email = #{userEmail}

SELECT id, title, start_date AS startDate, end_date AS endDate, category, user_email
FROM calendar_events
WHERE id = #{id}

mapper.update:
UPDATE calendar_events 
SET title = #{title}, start_date = #{startDate}, end_date = #{endDate}, category = #{category}
WHERE id = #{id} AND user_email = #{userEmail}
