mapper.select:
select *
from USERS
where email = #{email} AND pw = #{pw}

mapper.update:
UPDATE USERS
SET pw =
#{pw},
name = #{name},
position_code = #{positionCode}
dept_code = #{deptCode}
WHERE email = #{email}

mapper.select:
SELECT *
FROM USERS


		(

			id LIKE '%' || #{searchKeyword} || '%'



				OR name LIKE '%' || #{searchKeyword} || '%'


				name LIKE '%' || #{searchKeyword} || '%'


		)



		AND position_code = #{positionCode}



		AND dept_code = #{deptCode}
